    /**
     * 文字水印
     * @param $imgSrc
     * @param $markText
     * @param $TextColor
     * @param $markPos
     * @param int $fontSize
     * @return bool|string
     */
    function setTextWater($imgSrc,$markText,$TextColor,$markPos,$fontSize = 30)
    {
        $fontType = public_path().'/font/ping.ttf';
        $srcInfo = @getimagesize($imgSrc);
        $srcImg_w    = $srcInfo[0];
        $srcImg_h    = $srcInfo[1];
        $markText = mb_convert_encoding($markText, "html-entities","utf-8" );
        $markedfilename= public_path()."/images/test/".time();

        switch ($srcInfo[2])
        {
            case 1:
                $srcim =imagecreatefromgif($imgSrc);
                $markedfilename.=".gif";
                break;
            case 2:
                $srcim =imagecreatefromjpeg($imgSrc);
                $markedfilename.=".jpg";
                break;
            case 3:
                $srcim =imagecreatefrompng($imgSrc);
                $markedfilename.=".png";
                break;
            default:
                echo "不支持的图片文件类型";
                return false;
        }
        {

            if(!empty($markText))
            {
                if(!file_exists($fontType))
                {
                    echo '字体文件不存在';
                    return false;
                }
            }
            else {
                echo '没有水印文字';
                return false;
            }
            //此函数返回一个含有8个单元的数组表示文本外框的四个角，索引值含义：0代表左下角 X 位置，1代表坐下角 Y 位置，
            //2代表右下角 X 位置，3代表右下角 Y 位置，4代表右上角 X 位置，5代表右上角 Y 位置，6代表左上角 X 位置，7代表左上角 Y 位置
            $box = @imagettfbbox($fontSize, 0, $fontType,$markText);
            //var_dump($box);exit;
            $logow = max($box[2], $box[4]) - min($box[0], $box[6]);
            $logoh = max($box[1], $box[3]) - min($box[5], $box[7]);
        }



        switch($markPos)
        {
            case 1:
                $x = 5;
                $y = $fontSize;
                break;
            case 2:
                $x = ($srcImg_w - $logow) / 2;
                $y = $fontSize;
                break;
            case 3:
                $x = $srcImg_w - $logow - 5;
                $y = $fontSize;
                break;
            case 4:
                $x = $fontSize;
                $y = ($srcImg_h - $logoh) / 2;
                break;
            case 5:
                $x = ($srcImg_w - $logow) / 2;
                $y = ($srcImg_h - $logoh) / 2;
                break;
            case 6:
                $x = $srcImg_w - $logow - 5;
                $y = ($srcImg_h - $logoh) / 2;
                break;
            case 7:
                $x = $fontSize;
                $y = $srcImg_h - $logoh - 5;
                break;
            case 8:
                $x = ($srcImg_w - $logow) / 2;
                $y = $srcImg_h - $logoh - 5;
                break;
            case 9:
                $x = $srcImg_w - $logow - 5;
                $y = $srcImg_h - $logoh -5;
                break;
            default:
                $x = rand ( 0, ($srcImg_w - $logow) );
                $y = rand ( 0, ($srcImg_h - $logoh) );
        }

        $dst_img = @imagecreatetruecolor($srcImg_w, $srcImg_h);

        imagecopy ( $dst_img, $srcim, 0, 0, 0, 0, $srcImg_w, $srcImg_h);


        {
            $rgb = explode(',', $TextColor);

            $color = imagecolorallocate($dst_img, intval($rgb[0]), intval($rgb[1]), intval($rgb[2]));
            imagettftext($dst_img, $fontSize, 0, $x, $y, $color, $fontType,$markText);
        }


        switch ($srcInfo[2])
        {
            case 1:
                imagegif($dst_img,$markedfilename);
                break;
            case 2:
                imagejpeg($dst_img,$markedfilename);
                break;
            case 3:
                imagepng($dst_img,$markedfilename);
                break;
            default:
                echo ("不支持的水印图片文件类型");
                return false;
        }

        imagedestroy($dst_img);
        imagedestroy($srcim);

        return $markedfilename;
    }

    /**
     * 图片水印
