     * @param $markImg
     * @param $markPos
     * @return bool
     */
    function  setImgWater($imgSrc,$markImg,$markPos)
    {
        $srcInfo = @getimagesize($imgSrc);
        $srcImg_w    = $srcInfo[0];
        $srcImg_h    = $srcInfo[1];
        $img_water = "./waterpic";   //生成的图片文件名
        switch ($srcInfo[2])
        {
            case 1:
                $srcim =imagecreatefromgif($imgSrc);
                $img_water.=".gif";
                break;
            case 2:
                $srcim =imagecreatefromjpeg($imgSrc);
                $img_water.=".jpg";
                break;
            case 3:
                $srcim =imagecreatefrompng($imgSrc);
                $img_water.=".png";
                break;
            default:
                echo "不支持的图片文件类型";
                return false;
        }
        {
            if(!file_exists($markImg) || empty($markImg))
            {
                echo '水印文件不存在';
                return false;
            }

            $markImgInfo = @getimagesize($markImg);
            $markImg_w    = $markImgInfo[0];
            $markImg_h    = $markImgInfo[1];

            if($srcImg_w < $markImg_w || $srcImg_h < $markImg_h)
            {
                echo '水印文件比源图片文件都还大';
                return false;
            }

            switch ($markImgInfo[2])
            {
                case 1:
                    $markim =imagecreatefromgif($markImg);
                    break;
                case 2:
                    $markim =imagecreatefromjpeg($markImg);
                    break;
                case 3:
                    $markim =imagecreatefrompng($markImg);
                    break;
                default:
                    echo ("不支持的水印图片文件类型");
                    return false;
            }

            $logow = $markImg_w;
            $logoh = $markImg_h;
        }


        switch($markPos)
        {
            case 1:
                $x = 5;
                $y = 5;
                break;
            case 2:
                $x = ($srcImg_w - $logow) / 2;
                $y = 5;
                break;
            case 3:
                $x = $srcImg_w - $logow - 5;
                $y = 5;
                break;
            case 4:
                $x = 5;
                $y = ($srcImg_h - $logoh) / 2;
                break;
            case 5:
                $x = ($srcImg_w - $logow) / 2;
                $y = ($srcImg_h - $logoh) / 2;
                break;
            case 6:
                $x = $srcImg_w - $logow - 5;
                $y = ($srcImg_h - $logoh) / 2;
                break;
            case 7:
                $x = 5;
                $y = $srcImg_h - $logoh - 5;
                break;
            case 8:
                $x = ($srcImg_w - $logow) / 2;
                $y = $srcImg_h - $logoh - 5;
                break;
            case 9:
                $x = $srcImg_w - $logow - 5;
                $y = $srcImg_h - $logoh -5;
                break;
            default:
                $x = rand ( 0, ($srcImg_w - $logow) );
                $y = rand ( 0, ($srcImg_h - $logoh) );
        }

        $dst_img = @imagecreatetruecolor($srcImg_w, $srcImg_h);

        imagecopy ( $dst_img, $srcim, 0, 0, 0, 0, $srcImg_w, $srcImg_h);

        {
            imagecopy($dst_img, $markim, $x, $y, 0, 0, $logow, $logoh);
            imagedestroy($markim);
        }

        switch ($srcInfo[2])
        {
            case 1:
                imagegif($dst_img, $img_water );
                break;
            case 2:
                imagejpeg($dst_img, $img_water );
                break;
            case 3:
                imagepng($dst_img, $img_water );
                break;
            default:
                echo ("不支持的水印图片文件类型");
                return false;
        }

        imagedestroy($dst_img);
        imagedestroy($srcim);

        echo 'success,the watermarked picture is '.$img_water."\n";
        return true;
    }
}